import { OnInit, EventEmitter } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { map } from 'rxjs/operators';
import { PaisesModel } from '../models/Paises.model';
import { RespuestaPaises } from '../models/RespuesaPaises';

@Injectable({
  providedIn: 'root'
})
export class PaisesService {

  constructor(
    private _http: HttpClient
  ) { 
  }

  getPaises(){
    return this._http.get<RespuestaPaises[]>('https://restcountries.com/v3.1/all')
    .pipe(
      map(this.filtrarPais)
    );
  }

  filtrarPais(response: RespuestaPaises[]){
    return  response.map(function(item){
      return { nombre: item.name.common, codigo: item.altSpellings[0] };
    })
  }

}
