import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PaisesModel } from 'src/app/core/models/Paises.model';
import { PaisesService } from 'src/app/core/paises/paises.service';
import { ValidadorFormUsuariosService } from 'src/app/core/validador-form-usuarios/validador-form-usuarios.service';

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
})
export class UsuariosComponent implements OnInit {

  userForm: FormGroup;
  paises: PaisesModel[] = [];
  loading: boolean = true;

  public constructor(
    private _paisesService: PaisesService,
    private _fb: FormBuilder,
    private _validadorFormUsuariosService: ValidadorFormUsuariosService
  ){

    this.userForm = this._fb.group({
      uid: [null],
      userName: ['', [Validators.required, this._validadorFormUsuariosService.requiredInput('user name'), this._validadorFormUsuariosService.minLength(3, 'user name')]],
      firstName: ['', [Validators.required, this._validadorFormUsuariosService.requiredInput('first name'), this._validadorFormUsuariosService.minLength(5, 'first name')]],
      lastName: ['', [Validators.required, this._validadorFormUsuariosService.requiredInput('last name'), this._validadorFormUsuariosService.minLength(4, 'last name')]],
      email: ['', [Validators.required, this._validadorFormUsuariosService.requiredInput('email'), this._validadorFormUsuariosService.email]],
      age: [1, [Validators.required, Validators.min(1), Validators.max(100)]],
      gender: [null, [Validators.required, Validators.min(1), Validators.max(100)]],
    });

  }

  ngOnInit() {
      this.getPaises();  
  }

  getPaises(){
    this._paisesService.getPaises().subscribe((data: any) => {
      this.paises = data;
      // tiempo para ver loading
      setTimeout(() => {
        this.loading = false;
      }, 1500);
    })
  }

}
