import { Component, OnInit } from '@angular/core';
import { PaisesModel } from 'src/app/core/models/Paises.model';
import { PaisesService } from 'src/app/core/paises/paises.service';

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
})
export class UsuariosComponent implements OnInit {

  paises: PaisesModel[] = [];
  loading: boolean = false;

  public constructor(
    private _paisesService: PaisesService
  ){

  }

  ngOnInit() {
      this.getPaises();  
  }

  getPaises(){
    this.loading = true;
    this._paisesService.getPaises().subscribe((data: any) => {
      this.loading = false
      this.paises = data;
    })
  }

}
