import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PaisesModel } from 'src/app/core/models/Paises.model';
import { PaisesService } from 'src/app/core/paises/paises.service';
import { ValidadorFormUsuariosService } from 'src/app/core/validador-form-usuarios/validador-form-usuarios.service';

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
})
export class UsuariosComponent implements OnInit {

  userForm: FormGroup;
  paises: PaisesModel[] = [];
  loading: boolean = true;

  public constructor(
    private _paisesService: PaisesService,
    private _fb: FormBuilder,
    private _validadorFormUsuariosService: ValidadorFormUsuariosService
  ){

    this.userForm = this._fb.group({
      uid: [null],
      nombre: ['', [Validators.required, this._validadorFormUsuariosService.requerido('Nombre'), this._validadorFormUsuariosService.minimo(3, 'Nombre')]],
      Apellido: ['', [Validators.required, this._validadorFormUsuariosService.requerido('Apellido'), this._validadorFormUsuariosService.minimo(3, 'Apellido')]],
      correo: ['', [Validators.required, this._validadorFormUsuariosService.requerido('Correo Electrónico'), this._validadorFormUsuariosService.email]],
      fechaNacimiento: ['', [Validators.required, this._validadorFormUsuariosService.requerido('Fecha Nacimiento')]],
      telefono: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(10),Validators.pattern('/^[1-9]\d{6,10}$/'), this._validadorFormUsuariosService.requerido('Teléfono')]],
      paisResidencia: ['', [Validators.required, this._validadorFormUsuariosService.requerido('País Residencia')]],
      contacto: [false, [Validators.required]],
    });

  }

  ngOnInit() {
      this.getPaises();  
  }

  getPaises(){
    this._paisesService.getPaises().subscribe((data: any) => {
      console.log(data);

      this.paises = data;
      // tiempo para ver loading
      setTimeout(() => {
        this.loading = false;
      }, 1500);
    })
  }

  submitForm() {

    if (this.userForm.valid) {
      this.loading = true;
      (this.userForm.value.uid) ? this.editarUsuario() : this.agregarUsuario();
      this.userForm.reset();
      this.loading = false;
    }

  }

  agregarUsuario(){
    console.log(this.userForm);

  }

  editarUsuario(){

  }

}
